mapper.update:
UPDATE `edu_lesson_class_common_homework`
SET commit_num = commit_num + 1
WHERE id = #{id}

mapper.select:
SELECT `id`, `title`, IF(is_publish != 1, is_publish, IF(`end_time` = NOW(), 1, 3)) AS `status` FROM `edu_lesson_class_common_homework` WHERE `lesson_class_id` = #{classId}
AND `is_publish` = #{isPublish}
ORDER BY `create_time` DESC

SELECT 1
FROM `edu_lesson_class_common_homework` h,
     `edu_lesson_class_stu` s
WHERE h.`id` = #{id}
  AND h.`lesson_class_id` = s.`lesson_class_id`
  AND s.`stu_id` = #{stuId}
